          	// Added temporary until Switch.MultiLevel.GetCount works properly 
            for (int i = 1; i <= 5 ; i++)
            {
                string address = m.Instance.Address + "." + i.ToString();
                Program.AddVirtualModule(m.Instance.Domain, address, "Dimmer", "homegenie/generic/dimmer");
            }
          	// End of Added
	    }
      	return false ;
	});
});

