// We want to do further processing whenever a module changes 
When.ModuleParameterChanged( (module, property) => {
  
  	if (module.HasFeature("EnergyManagement.EnergySavingMode") && module.IsOfDeviceType("Dimmer") && property.Name == "Status.Level")
	{
        var level = module.Parameter("Status.Level");
        var newlevel = Program.InputField("MaxLevel").DecimalValue;
        //
        if (level.DecimalValue > (newlevel / 100D)) 
        {
            Program.Notify("Energy Saving Mode", module.Instance.Address + "<br>" + module.Instance.Name + " set to " + newlevel + "%");
            module.Level = newlevel;
            return false;
        }
    }
  	// returning true, will route event to other listeners
  	return true;
});

Program.GoBackground();

