ProgramBlock.ScriptSource:
var pinDta = ConnectorPin.P1Pin07.ToProcessor();
var pinClk = ConnectorPin.P1Pin13.ToProcessor();

var driver = GpioConnectionSettings.DefaultDriver;

driver.Allocate(pinDta, PinDirection.Output);
driver.Allocate(pinClk, PinDirection.Output);

When.ProgramStopping(()=>{
  ((IDisposable)driver).Dispose();
  return true;
});

//init 
driver.Write(pinDta, false);
Raspberry.Timers.HighResolutionTimer.Sleep(0.000001m);


for(int i = 0; i < 4; i++)
{
  	driver.Write(pinDta, true);
	driver.Write(pinDta, false);
}

var sendData16 = new Func<uint, bool>((data)=>{
  // send 16 bit data
   for(int i=0; i<16; i++)
  {
    bool state = ((data&0x8000) > 0 ? true : false);
    driver.Write(pinDta, state);
    state = driver.Read(pinClk) ? false : true;
    driver.Write(pinClk, state);
    data <<= 1;
  }
  return true;
});

var latchData = new Func<bool>(()=>{
    driver.Write(pinDta, false);
	Raspberry.Timers.HighResolutionTimer.Sleep(0.000001m);
    for(int i=0; i<4; i++)
    {
         driver.Write(pinDta, true);
         driver.Write(pinDta, false);
    }
  	return true;
});

uint CMDMODE = 0x0000;


var setLeds = new Func<string, bool>((leds)=>{
    sendData16(CMDMODE);
    uint index_bits = (uint)Convert.ToInt32(leds, 2);
    for (int i=0;i<12;i++)
    {
        uint state = (uint)((index_bits&0x0001) > 0 ? 0x00FF : 0x0000);
        sendData16(state);
        index_bits = index_bits>>1;
    }
    latchData();
  	return true;
});

setLeds("1000000000");
//Pause(.1);
setLeds("0100000000");
//Pause(.1);
setLeds("0010000000");
//Pause(.1);
setLeds("0001000000");
//Pause(.1);
setLeds("0000100000");
//Pause(.1);
setLeds("0000010000");
//Pause(.1);
setLeds("0000001000");
//Pause(.1);
setLeds("0000000100");
//Pause(.1);
setLeds("0000000010");
//Pause(.1);
setLeds("0000000001");
//Pause(.1);


Pause(3);


var setLevel = new Func<int, bool>((level)=>{
    sendData16(CMDMODE);
     for(int i=0;i<12;i++)
    {
        uint state1 = (uint)((i< level) ? 0x00FF : 0x0000);
        sendData16(state1);
    }
    latchData();
  	return true;
});

for (int z = 0; z < 1000; z++)
{
	setLevel(z % 11);
}

