SchedulerItem.Script:
timeout = 1;
domain = "HomeAutomation.Demo";
address = "1";
parameter = "Sensor.MotionDetect";

var idleTime = 0;
var sensor = $$.modules.inDomain(domain).withAddress(address).get();
if (sensor.Exists)
  idleTime = sensor.Parameter(parameter).idleTime;

if (idleTime > timeout*60 && $$.data('status') == '' && $$.boundModules.isOn) {
  $$.boundModules.off();
  $$.data('status', 'restore');
} else if (idleTime < timeout*60 && $$.data('status') == 'restore') {
  $$.boundModules.on();
  $$.data('status', '');
}

$$.onUpdate(function(m,p){
  if (m.Instance.Domain == domain && m.Instance.Address == address 
      && p.name == parameter && $$.data('status') == 'restore') {
    $$.boundModules.on();
    $$.data('status', '');
  }
});

// Turn on, if previous minute was not in schedule (start occurrence range)
if (!$$.onPrevious()) 
  $$.boundModules.on();
// Turn off, if next minute won't be in schedule (end occurrence range)
if ($$.onPrevious() && !$$.onNext()) 
  $$.boundModules.off();

// Thermostat mode to set
var mode = 'Cool';
// Set mode to Cool, if previous minute was not in schedule (start occurrence range)
if (!$$.onPrevious()) 
  $$.boundModules
  .command('Thermostat.ModeSet')
  .set(mode);
// Turn off, if next minute won't be in schedule (end occurrence range)
if ($$.onPrevious() && !$$.onNext()) 
  $$.boundModules
  .command('Thermostat.ModeSet')
  .set('Off');

// Thermostat mode to set
var mode = 'CoolEconomy';
// Set mode to Cool, if previous minute was not in schedule (start occurrence range)
if (!$$.onPrevious()) 
  $$.boundModules.command('Thermostat.ModeSet').set(mode);
// Turn off, if next minute won't be in schedule (end occurrence range)
if ($$.onPrevious() && !$$.onNext()) 
  $$.boundModules.command('Thermostat.ModeSet').set('Off');

// Thermostat mode to set
var mode = 'Heat';
// Set mode to Cool, if previous minute was not in schedule (start occurrence range)
if (!$$.onPrevious()) 
  $$.boundModules
  .command('Thermostat.ModeSet')
  .set(mode);
// Turn off, if next minute won't be in schedule (end occurrence range)
if ($$.onPrevious() && !$$.onNext()) 
  $$.boundModules
  .command('Thermostat.ModeSet')
  .set('Off');

// Thermostat mode to set
var mode = 'HeatEconomy';
// Set mode to Cool, if previous minute was not in schedule (start occurrence range)
if (!$$.onPrevious()) 
  $$.boundModules
  .command('Thermostat.ModeSet')
  .set(mode);
// Turn off, if next minute won't be in schedule (end occurrence range)
if ($$.onPrevious() && !$$.onNext()) 
  $$.boundModules
  .command('Thermostat.ModeSet')
  .set('Off');
